import { useEffect, useState,  useNavigate } from 'react';
import api from '../../services/api'; // ✅ Using your configured Axios instance

const TeacherHome = () => {

   const navigate = useNavigate();

  useEffect(() => {
    if (application?.status === 'accepted') {
      navigate('/teacher/dashboard');
    }
  }, [application]);

  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    qualifications: '',
    subjects: '',
    bio: '',
  });
  
  const [message, setMessage] = useState('');

  // Fetch teacher application status
  useEffect(() => {
    const fetchApplication = async () => {
      try {
        const res = await api.get('/teacher/application');
        setApplication(res.data);
      } catch (err) {
        console.error('Failed to fetch application:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchApplication();
  }, []);

  //  Input handler
  const handleChange = (e) => {
    setForm((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  //  Submit mentor application
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedForm = {
        ...form,
        subjects: typeof form.subjects === 'string'
          ? form.subjects.split(',').map((s) => s.trim())
          : form.subjects,
      };

      const res = await api.post('/teacher/apply', formattedForm);
      setMessage(res.data.message);
      setApplication({ applied: true, status: 'pending' }); // Immediately reflect status
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || 'Application failed');
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Teacher Dashboard</h2>

      {message && <p style={{ color: 'blue' }}>{message}</p>}

      {/* Show form if not applied */}
      {!application?.applied ? (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem', maxWidth: '400px' }}>
          <div>
            <label>Qualifications:</label>
            <input
              type="text"
              name="qualifications"
              value={form.qualifications}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Subjects (comma separated):</label>
            <input
              type="text"
              name="subjects"
              value={form.subjects}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Bio:</label>
            <textarea
              name="bio"
              value={form.bio}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit">Apply as Mentor</button>
        </form>
      ) : (
        <div>
          <p>
            You have already applied to become a mentor.
          </p>
          <p>
            Current application status: <strong>{application.status}</strong>
          </p>
          
        </div>
      )}
    </div>
  );
};

export default TeacherHome;
