import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ students: 0, teachers: 0 });
  const [applications, setApplications] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchStats();
    fetchApplications();
  }, []);

  const fetchStats = async () => {
    try {
      const res = await api.get('/admin/stats', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStats(res.data);
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  const fetchApplications = async () => {
    try {
      const res = await api.get('/admin/teachers', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setApplications(res.data);
    } catch (err) {
      console.error('Error fetching applications:', err);
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await api.patch(`/admin/teacher/${id}`, { status }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchApplications(); // Refresh list
    } catch (err) {
      console.error('Error updating status:', err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Admin Dashboard</h2>

      <div style={{ marginBottom: '2rem' }}>
        <p>Total Students: <strong>{stats.students}</strong></p>
        <p>Total Teachers: <strong>{stats.teachers}</strong></p>
      </div>

      <h3>Teacher Applications</h3>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Qualifications</th>
            <th>Subjects</th>
            <th>Bio</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {applications.map(app => (
            <tr key={app._id}>
              <td>{app.user?.name}</td>
              <td>{app.user?.email}</td>
              <td>{app.status}</td>
              <td>{app.qualifications}</td>
              <td>{app.subjects.join(', ')}</td>
              <td>{app.bio}</td>
              <td>
  {app.status === 'pending' ? (
    <div style={{ display: 'flex', gap: '0.5rem' }}>
      <button
        onClick={() => updateStatus(app._id, 'accepted')}
        style={{ backgroundColor: '#4CAF50', color: 'white', padding: '0.3rem 0.6rem', border: 'none', borderRadius: '4px' }}
      >
        ✅ Approve
      </button>
      <button
        onClick={() => updateStatus(app._id, 'rejected')}
        style={{ backgroundColor: '#f44336', color: 'white', padding: '0.3rem 0.6rem', border: 'none', borderRadius: '4px' }}
      >
        ❌ Reject
      </button>
    </div>
  ) : (
    <span style={{
      color: app.status === 'accepted' ? '#4CAF50' : '#f44336',
      fontWeight: 'bold'
    }}>
      {app.status === 'accepted' ? '✔️ Approved' : '❌ Rejected'}
    </span>
  )}
</td>

            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
