import React, { useEffect, useState } from "react";
import api from "../../services/api";
import Navbar from "../../components/Navbar";

const StudentHome = () => {
  const [profile, setProfile] = useState(null);
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    grade: "",
    institution: "",
    rollNo: ""
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserAndProfile = async () => {
      try {
        const res = await api.get("/student/me", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          }
        });

        setUser(res.data.user);
        setProfile(res.data.profile);
      } catch (err) {
        console.error("Error fetching student data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserAndProfile();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await api.post("/student/update", formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      });

      setUser(res.data.user);
      setProfile(res.data.profile);
    } catch (err) {
      console.error("Error updating profile:", err);
    }
  };

  if (loading) return <div className="text-center mt-10">Loading...</div>;

  return (
    <>
      <Navbar name={user?.name} rollNo={profile?.rollNo} />

      <div className="p-6">
        {!user?.isProfileComplete ? (
          <div className="max-w-md mx-auto bg-white p-6 rounded shadow">
            <h2 className="text-xl font-semibold mb-4">Complete Your Profile</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                name="grade"
                placeholder="Grade"
                value={formData.grade}
                onChange={handleChange}
                required
                className="w-full border p-2 rounded"
              />
              <input
                type="text"
                name="institution"
                placeholder="Institution"
                value={formData.institution}
                onChange={handleChange}
                required
                className="w-full border p-2 rounded"
              />
              <input
                type="text"
                name="rollNo"
                placeholder="Roll Number"
                value={formData.rollNo}
                onChange={handleChange}
                required
                className="w-full border p-2 rounded"
              />
              <button
                type="submit"
                className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
              >
                Submit
              </button>
            </form>
          </div>
        ) : (
          <div className="text-center mt-8">
            <h3 className="text-2xl font-bold mb-2">Welcome to your Dashboard, {user.name}!</h3>
            <p className="text-gray-600">Use the options to enroll in tests, view results, and more.</p>
          </div>
        )}
      </div>
    </>
  );
};

export default StudentHome;
