import { useEffect, useState } from 'react';
import axios from 'axios';
import api from '../../services/api';
const TeacherHome = () => {
  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    qualifications: '',
    subjects: '',
    bio: '',
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchApplication = async () => {
      try {
        const res = await axios.get('/api/teacher/application', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setApplication(res.data);
      } catch (err) {
        console.error('Failed to fetch application:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchApplication();
  }, []);

  const handleChange = e => {
    setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/teacher/apply', form, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      setMessage(res.data.message);
      setApplication({ applied: true, status: 'pending' });
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || 'Application failed');
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Teacher Dashboard</h2>

      {message && <p>{message}</p>}

      {!application?.applied ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Qualifications:</label>
            <input
              type="text"
              name="qualifications"
              value={form.qualifications}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Subjects (comma separated):</label>
            <input
              type="text"
              name="subjects"
              value={form.subjects}
              onChange={(e) =>
                setForm(prev => ({
                  ...prev,
                  subjects: e.target.value.split(',').map(s => s.trim())
                }))
              }
              required
            />
          </div>
          <div>
            <label>Bio:</label>
            <textarea
              name="bio"
              value={form.bio}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Apply as Mentor</button>
        </form>
      ) : (
        <p>Your application is <strong>{application.status}</strong>.</p>
      )}
    </div>
  );
};

export default TeacherHome;
