import React, { useEffect, useState } from "react";
import api from "../../services/api";
import Navbar from "../../components/Navbar";

const StudentHome = () => {
  const [profile, setProfile] = useState(null);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({ name: "", rollNo: "" });

  // Fetch profile on load
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await api.get("/student/me", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        if (res.data && res.data.isProfileComplete) {
          setProfile({ name: res.data.name, rollNo: res.data.rollNo });
          setIsProfileComplete(true);
        } else {
          setIsProfileComplete(false);
        }
      } catch (err) {
        console.error("Error fetching profile:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleProfileSubmit = async () => {
    try {
      const res = await api.post(
        "/student/update",
        formData,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      // After successful update, re-fetch profile
      setProfile({ name: res.data.name, rollNo: res.data.rollNo });
      setIsProfileComplete(true);
    } catch (err) {
      console.error("Error updating profile:", err);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <>
      <Navbar name={profile?.name} rollNo={profile?.rollNo} />

      <div style={{ padding: "2rem" }}>
        {isProfileComplete ? (
          <>
            <h3>Welcome {profile.name}</h3>
            <p>Your profile is verified âœ…</p>
            <p>Use the options to enroll in tests, view results, and more.</p>
          </>
        ) : (
          <>
            <h3>Complete Your Profile</h3>
            <input
              type="text"
              name="name"
              placeholder="Enter your name"
              value={formData.name}
              onChange={handleInputChange}
            />
            <br />
            <input
              type="text"
              name="rollNo"
              placeholder="Enter your roll number"
              value={formData.rollNo}
              onChange={handleInputChange}
            />
            <br />
            <button onClick={handleProfileSubmit}>Submit Profile</button>
          </>
        )}
      </div>
    </>
  );
};

export default StudentHome;
